import {ADD_TO_CART} from '../constants/actionTypes';

  const totalprice=(state=0,action)=>{

    switch(action.type){
        case ADD_TO_CART:
        return state.totalprice+action.totalprice
        default :
        return state
    }
}


export default totalprice;
