import {RECEIVE_PRODUCTS} from '../constants/actionTypes';

const initPState = {
    products: [],
    buyProductById:[]
  }
  const products=(state=initPState.products,action)=>{
    switch(action.type){
        case RECEIVE_PRODUCTS:
        return action.products
        default:
        return state
    }
  }


  export default products;
