import React from 'react';

const Cart=({img,title,price})=>{

    const imgStyle={
        width:'100px',
        height:'267px'
    }
    const wrapStyle={
        width:'350px',
        height:'100%',
        margin:'0 auto',
        display:'flex',
        flexWrap:'wrap'
    }
    return <div style={wrapStyle}>
       <img /* src={require('../static/'+img)} */ style={imgStyle}></img>
       <h2>{title}</h2>
       <div>{price}</div>
    </div>
}

export default Cart;
