import {ADD_TO_CART} from '../constants/actionTypes';


var n=1;
const currentNums=(state=[],action)=>{
    switch(action.type){
        case ADD_TO_CART:
       
        return [action.cartProduct.id,++n]
        
        default:
        return state
    }
}
export default currentNums;
