import * as types from '../constants/actionTypes';
import shop from '../apis/shop';





const receiveSize=size=>({
    type:types.RECEIVE_SIZE,
    size
})
export const getAllSize=()=>dispatch=>{
    shop.getSize(
        size=>{
          dispatch(receiveSize(size))
        }
    )
}
const receiveProducts=products=>({
    type:types.RECEIVE_PRODUCTS,
    products
})
export const getAllProducts=()=>dispatch=>{
    shop.getProducts(
        products=>{
            dispatch(receiveProducts(products))
        }
    )
}
const getSortProduct=(avaiProduct)=>({
 type:'SELECT_TYPE',
 avaiProduct
})
export const onChangedSort=(S,e)=>dispatch=>{
   dispatch(getSortProduct(e))
}

const toCart=(cartProduct,products)=>({
    type:types.ADD_TO_CART,
    cartProduct,
    currentInventory:--cartProduct.inventory,
    products,
  
    
    
})

export const addToCart=(cartProduct,products)=>dispatch=>{
   dispatch(toCart(cartProduct,products))
}
