import {RECEIVE_PRODUCTS,ADD_TO_CART,SELECT,HIGHEST_TO_LOWEST,LOWEST_TO_HIGHEST} from '../constants/actionTypes';

const initPState = {
    products: []
  
  }
  const products=(state=initPState.products,action)=>{
    switch(action.type){
        case RECEIVE_PRODUCTS:
        return action.products 
        case ADD_TO_CART:
        return state
        case HIGHEST_TO_LOWEST:
        return state
        case LOWEST_TO_HIGHEST:
        return state
        default:
        return state
    }
  }


  export default products;
