import React from 'react';
import Product from './Product';
import {addToCart} from '../actions';
const ProductItem =({product,addToCart,products})=>{
    var buttonShow;
    if(product.inventory==0){
        buttonShow='disabled'
    }
    return <div style={{margin:'20px',textAlign:'center'}}>
        <Product
        img={product.img}
        title={product.title}
        price={product.price}
        products={products}
        />
        <button style={{width:'200px',height:'35px',border:'none',backgroundColor:'brown',color:'white'}}
                disabled={buttonShow}
           onClick={()=>addToCart(product,products)}
        >
        { parseInt(product.inventory)==0?
          <div disabled='disabled'>soled out</div> :<div>add to cart{product.inventory}</div> 
        
        }
        </button>
    </div>
}
const mapStateToProps=state=>({
    products:state.products,
 
})
export default connect(mapStateToProps) (ProductItem);
