import React from 'react';
import Size from '../components/Size';
import {addSize} from '../actions';
import {connect} from 'react-redux';
const SizeContainer=({selectSize,addSize,products})=>(
    <div style={{width:'200px',height:'60px',display:'flex',flexWrap:'wrap'}}>
    <div style={{width:'200px',marginBottom:'15px',fontWeight:'bold'}}>Size:</div>
        
    <div style={{width:'200px',display:'flex',flexWrap:'wrap'}}>
        <Size size={'XS'} selectSize={selectSize} addSize={addSize} products={products}/>
        <Size size={'S'} selectSize={selectSize} addSize={addSize} products={products}/>
        <Size size={'M'} selectSize={selectSize} addSize={addSize} products={products}/>
        <Size size={'ML'} selectSize={selectSize} addSize={addSize} products={products}/>
        <Size size={'L'} selectSize={selectSize} addSize={addSize} products={products}/>
        <Size size={'XL'} selectSize={selectSize} addSize={addSize} products={products}/>
        <Size size={'XXL'} selectSize={selectSize} addSize={addSize} products={products}/>
        {console.log('selectSize:',selectSize)}
    </div>
      
    </div>
)

const mapStateToProps=state=>({
    selectSize:state.selectSize,
    products:state.products
  })
 export default connect(mapStateToProps,{addSize})(SizeContainer);
