import {RECEIVE_PRODUCTS,ADD_TO_CART,SELECT_TYPE} from '../constants/actionTypes';

const initPState = {
    products: []
  
  }
  const products=(state=initPState.products,action)=>{
    switch(action.type){
        case RECEIVE_PRODUCTS:
        return action.products 
        case ADD_TO_CART:
        return state
        case SELECT_TYPE:
        return action.avaiProducts
     
        return state
        default:
        return state
    }
  }


  export default products;
