import React from 'react';

const Product =({img,title,price})=>(
    <div>
        <img src={require(img)} alt={title} style={{width:'200px',height:'300px'}}/>
        <div>{title}</div>
        <div>&#36 {price}</div>
    </div>
)
export default Product;
