import React from 'react';
import Size from '../components/Size';
import {addSize} from '../actions';
const SizeContainer=({selectSize,addSize})=>(
    <div style={{width:'200px',height:'60px',display:'flex',flexWrap:'wrap'}}>
    <div style={{width:'200px',marginBottom:'15px',fontWeight:'bold'}}>Size:</div>
        
    <div style={{width:'200px',display:'flex',flexWrap:'wrap'}}>
        <Size size={'XS'} selectSize={selectSize} addSize={addSize}/>
        <Size size={'S'} selectSize={selectSize} addSize={addSize}/>
        <Size size={'M'} selectSize={selectSize} addSize={addSize}/>
        <Size size={'ML'} selectSize={selectSize} addSize={addSize}/>
        <Size size={'L'} selectSize={selectSize} addSize={addSize}/>
        <Size size={'XL'} selectSize={selectSize} addSize={addSize}/>
        <Size size={'XXL'} selectSize={selectSize} addSize={addSize}/>
    </div>

    </div>
)

const mapStateToProps=state=>({
    selectSize:state.selectSize
    
  })
 export default connect(mapStateToProps,{addSize})(SizeContainer);
