import React from 'react';
import {onChangedSort} from '../actions';
import { connect } from 'react-redux';


const SelectBox=({products,onChangedSort})=> {

  var arry1=products;
  arry1.sort(function(a,b){
    return a.price-b.price;
})
console.log('arr1',arry1);

   

        return <select style={{width:'200px',height:'35px'}} onChange={e=>onChangedSort(e)
        
        
        }>
            <option value='SELECT'>select</option>
            <option value='HIGHEST_TO_LOWEST'>Highest to Lowest</option>
            <option value='LOWEST_TO_HIGHT'>Lowest to Highest</option>
        </select>
    
}
const mapStateToProps=state=>({
    products:state.products,
   
 })
export default connect(mapStateToProps,{onChangedSort}) (SelectBox);
