import React from 'react';
import OnlyCartHeader from '../components/OnlyCartHeader';

const CartContainer=({showcart})=>{
   
    
  var getShow=showcart=>{
      if(showcart===false){
          return 'none'
      }else return 'block'
  };

 
        const cartStyle={
            width:'400px',
            height:'100%',
            textAlign:'center',
          
            position:'fixed',
            top:0,
            right:0,
            zIndex:999,
            display:getShow(showcart)
        }
        const wrapStyle={
            width:'400px',
            height:'100%',
            display:'flex',
            flexWrap:'wrap'
        }
        const contentStyle={
            width:'370px',
            height:'100%',
            backgroundColor:'black',
        }
        return <div style={cartStyle}>
               <div style={wrapStyle}>
                   <button style={{width:'30px',height:'20px',backgroundColor:'black'}}></button>
                 <div style={contentStyle}>  
                    <div style={{width:'50px',height:'50px',margin:'0 auto'}}>
                       <OnlyCartHeader/>{console.log('cartcontainer_showcart',showcart)}
                    </div>
                 </div>   
              </div>
        </div>
    }

export default CartContainer;
