import React from 'react';


const OnlyCartHeader=({productsQuans})=>{
    var show;
    if(productsQuans>0){
       show='block'
    }else{
        show='none'
    }
    const wrapStyle={
        width:'100px',
        height:'80px',
        backgroundColor:'black',
  }

  const showStyle={
    position:'absolute',
    width:'100px',
    height:'80px',
    textAlign:'center',
  }
    const iStyle={
   
        fontSize:'70px',
        color:'white',
        zIndex:'-1'
    }
    const numStyle={
        display:{show},
        width:'30px',
        height:'30px',
        borderRadius:'100%',
        backgroundColor:'yellow',
        margin:'0 auto',
        alignItems:'center',
        bottom:'30px',
        zIndex:'999'
    }
   return <div style={wrapStyle}>
      <div style={showStyle}>
        <i class="fa fa-shopping-cart" style={iStyle}></i>
        <div style={numStyle}>{productsQuans}</div>
      </div>
    </div>
    
}

export default OnlyCartHeader;
