import {RECEIVE_PRODUCTS,ADD_TO_CART} from '../constants/actionTypes';

const initPState = {
    products: []
  
  }
  const products=(state=initPState.products,action)=>{
    switch(action.type){
        case RECEIVE_PRODUCTS:
        return action.products
        case ADD_TO_CART:{
          state[action.cartProduct.id-1].inventory-1
       /*    list[action.cartProduct.id-1].inventory=action.products[action.cartProduct.id-1]-1 */
        return state
        }
        default:
        return state
    }
  }


  export default products;
