import * as types from '../constants/actionTypes';
import shop from '../apis/shop';



const receiveSize=size=>({
    type:types.RECEIVE_SIZE,
    size
})
export const getAllSize=()=>dispatch=>{
    shop.getSize(
        size=>{
          dispatch(receiveSize(size))
        }
    )
}
const receiveProducts=products=>({
    type:types.RECEIVE_PRODUCTS,
    products
})
export const getAllProducts=()=>dispatch=>{
    shop.getProducts(
        products=>{
            dispatch(receiveProducts(products))
        }
    )
}



const toCart=(cartProduct)=>({
    type:types.ADD_TO_CART,
    cartProduct,
    inventory:cartProduct.inventory-->0
})

export const addToCart=(cartProduct)=>dispatch=>{
   dispatch(toCart(cartProduct))
}
