import RECEIVE_SIZE from '../constants/actionTypes';
const initialState = {
    size: [],
    
  }
const size = (state=initialState.size,action)=>{
    switch(action.type){
        case RECEIVE_SIZE:
        return state
        default:
        return state
    }
}

export default size;
