import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './containers/App';
import * as serviceWorker from './serviceWorker';
import thunk from 'redux-thunk';
import { createLogger } from 'redux-logger';
import reducer from './reducers';
import {getAllSize,getAllProducts} from './actions';
import { createStore, applyMiddleware } from 'redux';
import { Provider } from 'react-redux';

const middleware = [ thunk ];
if (process.env.NODE_ENV !== 'production') {
  middleware.push(createLogger());
}
const store = createStore(
    reducer,
    applyMiddleware(...middleware)
  )
  store.dispatch(getAllProducts())
  store.dispatch(getAllSize())
  const getMyState=store.getState().showcart
  store.dispatch({type:'GET_SHOW_CART',getMyState})
ReactDOM.render(
      <Provider store={store}>
      <App getMyState={getMyState}/>{console.log('state:',store.getState())}
      </Provider>, document.getElementById('root'));


serviceWorker.unregister();
