import React from 'react';
import Product from './Product';

const ProductItem =({product,addToCart})=>{
    var buttonShow;
    if(product.inventory<=0){
        buttonShow='disabled'
    }
    return <div style={{margin:'20px',textAlign:'center'}}>
        <Product
        img={product.img}
        title={product.title}
        price={product.price}
        />
        <button style={{width:'200px',height:'35px',border:'none',backgroundColor:'brown',color:'white'}}
                disabled='disabled'
           onClick={()=>addToCart(product)}
        >
        { parseInt(product.inventory)==2?
          <div disabled='disabled'>soled out</div> :<div>add to cart{product.inventory}</div> 
        
        }
        </button>
    </div>
}

export default ProductItem;
