import React from 'react';


const OnlyCartHeader=({productsQuans})=>{
    var show;
    if(productsQuans>0){
       show='block'
    }else{
        show='none'
    }
    const iStyle={
        fontSize:'70px',
        color:'rgba(0, 0, 0, 0.08)'
    }
    const numStyle={
        width:'30px',
        height:'30px',
        redius:'100%',
        backgroundColor:'yellow'
    }
   return <div style={{backgroundColor:'black',width:'100px',height:'100px'}}>
        <i class="fa fa-shopping-cart" style={{iStyle}}/>
        <div style={{display:{show,numStyle}}}>{productsQuans}</div>
    </div>
    
}

export default OnlyCartHeader;
