import React from 'react';
import Product from './Product';

const ProductItem =({product})=>(
    <div style={{margin:'20px',textAlign:'center'}}>
        <Product
        img={product.img}
        title={product.title}
        price={product.price}
        />
        <button style={{width:'200px',height:'35px',border:'none',backgroundColor:'brown',color:'white'}}>
            add to cart
        </button>
    </div>
)

export default ProductItem;
