              btnStyle= {
                width:'30px',
                height:'30px',
                fontWeight:'bold',
                borderRadius:'50%',
                marginRight:'20px',
                marginBottom:'15px',
                border:'none',
                outline:'none',
                color:'white',
                backgroundColor:'brown'

            }
            break;
