import React from 'react';


const OnlyCartHeader=({productsQuans})=>{
    var show;
    if(productsQuans>0){
       show='block'
    }else{
        show='none'
    }
    const iStyle={
         textAlign:'center',
        fontSize:'70px',
        color:'rgba(255,255,255,0.08)'
    }
    const numStyle={
        display:{show},
        width:'30px',
        height:'30px',
        borderRedius:'100%',
        backgroundColor:'yellow',
        margin:'0 auto'
    }
   return <div style={{backgroundColor:'black',width:'100px',height:'100px'}}>
        <i class="fa fa-shopping-cart" style={iStyle}></i>
        <div style={numStyle}>{productsQuans}</div>
    </div>
    
}

export default OnlyCartHeader;
