import React from 'react';

const Size=({size,selectSize,addSize})=>{
 
      
    
  getSizeStyle=()=> {
      for(var j=0;j<selectSize.length;j++){
          if(size==selectSize[j]){
              return {
                width:'30px',
                height:'30px',
                fontWeight:'bold',
                borderRadius:'50%',
                marginRight:'20px',
                marginBottom:'15px',
                border:'none',
                outline:'none',
                color:'white',
                backgroundColor:'brown'

            }
            break;
          }else return {
            width:'30px',
            height:'30px',
            fontWeight:'bold',
            borderRadius:'50%',
            marginRight:'20px',
            marginBottom:'15px',
            border:'none',
            outline:'none'
        }
      }
    }

    /*     const btnStyle={
            width:'30px',
            height:'30px',
            fontWeight:'bold',
            borderRadius:'50%',
            marginRight:'20px',
            marginBottom:'15px',
            border:'none',
            outline:'none'
        } */
        return <button  
                   onClick={()=>addSize(size,selectSize)}
                   style={this.getSizeStyle}
                 >
                 {size}{console.log('sizelength',selectSize.length)}
        </button>
        
        
    
}

export default Size;
