import React from 'react';
import OnlyCartHeader from '../components/OnlyCartHeader';
import {getShowCart} from '../actions';
import { connect } from 'react-redux';
import Cart from '../components/Cart';

const CartContainer=({showcart,store,cartProduct})=>{
   
    
  var getShow=showcart=>{
      if(showcart===false){
          return 'none'
      }else return 'block'
  };

 
        const cartStyle={
            width:'400px',
            height:'100%',
            textAlign:'center',
          
            position:'fixed',
            top:0,
            right:0,
            zIndex:999,
            display:getShow(showcart)
        }
        const wrapStyle={
            width:'400px',
            height:'100%',
            display:'flex',
            flexWrap:'wrap'
        }
        const contentStyle={
            width:'370px',
            height:'100%',
            backgroundColor:'black',
        }
        const allItemStyle={
            width:'370px',
            height:'80%',
            backgroundColor:'black',
        }
        return <div style={cartStyle}>
               <div style={wrapStyle}>
                   <button style={{width:'30px',height:'30px',backgroundColor:'black',border:'none',margin:0,padding:0}}
                           onClick={()=>{  store.dispatch({type:'SHOW_CART',showcart})}}
                    
                   >
                   <i className="fa fa-close" style={{fontSize:'28px',color:'white'}}></i>
                   </button>
                 <div style={contentStyle}>  
                    <div style={{width:'50px',height:'50px',margin:'0 auto'}}>
                       <OnlyCartHeader/>{console.log('cartcontainer_showcart',showcart)}
                    </div>
               {<div style={allItemStyle}>
                   {cartProduct.map(
                       cartProductOne=>
                        <Cart
                        key={cartProductOne.id}
                        img={cartProductOne.img}
                        title={cartProductOne.title}
                        price={cartProductOne.price}
                    />
                   )}
                </div>
                }
                    {console.log('cartProduct___cart:',cartProduct)}
                 </div>   
              </div>
        </div>
    }
    const mapStateToProps=state=>({
       
        cartProduct:state.cartProduct
        
    })
    
export default connect(mapStateToProps)(CartContainer) ;
