import React from 'react';
import {connect} from 'react-redux';
import {addSize} from '../actions';
const Size=({addSize})=>{
 
 
  


        const btnStyle={
            width:'30px',
            height:'30px',
            fontWeight:'bold',
            borderRadius:'50%',
            marginRight:'20px',
            marginBottom:'15px',
            border:'none',
            outline:'none'
        }
        return <button  
                   onClik={addSize}
                   style={btnStyle}
                 >
                 {this.props.size}
        </button>
        
        
    
}

export default connect({addSize})(Size);
