import {SELECT,HIGHEST_TO_LOWEST,LOWEST_TO_HIGHEST} from '../constants/actionTypes';
const selectChange=(state=SELECT,action)=>{
    switch(action.type){
        case LOWEST_TO_HIGHEST:
        return action.LOWEST_TO_HIGHEST
        case HIGHEST_TO_LOWEST:
        return action.HIGHEST_TO_LOWEST
        default:
        return state

    }
}

export default selectChange;
