import {SHOW_CART} from '../constants/actionTypes';
const initShowState={
    showcart:false,
}
const showcart=(state=initShowState.show,action)=>{
    switch(action.type){
        case SHOW_CART:
        return !state.showcart
        default:
        return state
    }
}

export default showcart;
