import React from 'react';

const Cart=({img,title,price})=>{

    const imgStyle={
        width:'60px',
        height:'160px'
    }
    const wrapStyle={
        width:'350px',
        height:'160px',
        margin:'0 auto',
        display:'flex',
        flexWrap:'wrap',
        color:'white',
        marginTop:'10px',
        backgroundColor:'rgba(0,0,0,0.8)'

    }
    const titleStyle={
        width:'200px',
        height:'160px',
      /*   textAlign:'left' */
    }
    const priceStyle={
        color:'orange',
        display:'flex',
        width:'90px',
        height:'160px',
        textAlign:'left',
        alignItems:'center'
    }

    return <div style={wrapStyle}>

    {(img==null)?<div></div>:
       <img src={require('../static/'+img)} style={imgStyle}></img>
    }
      <div style={titleStyle}> 
          <p>{title}</p>{console.log('title',title)}{console.log('img',img)}
      </div>
       <div style={priceStyle}>
          {(price==null)?<div></div>:
          <div >&#36; {price}</div> 
          }
       </div>
    </div>
}

export default Cart;
