import {ADD_TO_CART} from '../constants/actionTypes';

const  initCPState={
    cartProduct:[]
}

const cartProduct=(state=initCPState.cartProduct,action)=>{
    switch(action.type){
        case ADD_TO_CART:
        return state.push(action.cartProduct)
        default:
        return state
    }
}

export default cartProduct;
