    else if(e.target.value===types.HIGHEST_TO_LOWEST){
        return p[2]
    }
    else return p[0]
}

const getSortProduct=(e,p)=>({
 type:'SELECT_TYPE',
 selectChange:e.target.value,
avaiProducts:p
})

const resolveSize=(size,selectSize)=>{
   
    if(selectSize.length==0){
        var initsize=[];
        initsize.push(size);
        return initsize;
    }else{
        var initsize=[size];
        var m=0;
       var p=[].concat(selectSize)
       for(var k=0;k<p.length;k++){
          if(p[k]==size){
              m++
          }
       }
       if(m>1)
        return p
    }
   

}

const getSelectSize=(s)=>({
